import React from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom'
import Cookies from 'js-cookie';
import Button from '@mui/material/Button';
import Radio from '@mui/material/Radio';
import {DataGrid} from '@mui/x-data-grid';
import {SERVER_URL} from '../constants.js'
const courseId = "";

class newAssignment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {assignmentName: "", dueDate: "", courseId: ""};

    }

    // Saves the new assignment 
    handleSubmit = () => {
        console.log("newAssignment.handleSubmit");
        const token = Cookies.get('XSRF-TOKEN');

        fetch(`${SERVER_URL}/course/${courseId}/assignment`, {
            method: 'POST', 
            headers: { 'Content-Type': 'application/json',
                       'X-XSRF-TOKEN': token }, 
            body: JSON.stringify({
                assignmentName: this.state.assignmentName,
                dueDate: this.state.dueDate,
                courseId: this.state.courseId })
        }).then(res => {
            if (res.ok) {
                toast.success("Assignment created.", {
                    position: toast.POSITION.BOTTOM_LEFT
                });
                this.setState({assignmentName: "", dueDate: "", courseId: ""})
                // Go back to assignment.js?
            } 
            else {
                toast.error("Unable to add assignment", {
                    position: toast.POSITION.BOTTOM_LEFT
                });
            }
        }).catch(err => {
            toast.error("Unable to add assignment", {
                position: toast.POSITION.BOTTOM_LEFT
            });
            console.error(err);
        });
    }

    render() {
        const columns = [
            { field: 'name', headerName: 'Name', width: 250, editable:true},
            { field: 'dueDate', headerName: 'Due Date', width: 250, editable:true},
            { field: 'courseId', headerName: 'Course Id', width: 150 , editable:true}
            ];
          
        return(
            <div align="left" >
                <h3>New Assignment:</h3>
                <form onSubmit={this.handleSubmit}>
                    <label>Name:</label>
                    <input type="text" name="assignmentName" value={this.state.assignmentName}></input>
                    <br></br>
                    <label>Due Date:</label>
                    <input type="text"  name="dueDate" value={this.state.dueDate}></input>
                    <br></br>
                    <label>Course Id:</label>
                    <input type="text"  name="courseId" value={this.state.courseId}></input>
                    <br></br>
                    <input type='submit' value='Submit'></input>
                </form>
                <ToastContainer autoClose={1500} />   
            </div>
        ); 
    };
}

export default newAssignment;

