import React from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom'
import Cookies from 'js-cookie';
import Button from '@mui/material/Button';
import Radio from '@mui/material/Radio';
import {DataGrid} from '@mui/x-data-grid';
import {SERVER_URL} from '../constants.js'

class newAssignment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {assignment:{ name: " ", dueDate: " ", courseId: " " }}
    }

    // Saves the new assignment 
    handleSave = () => {
        console.log("newAssignment.handleSave");
        const token = Cookies.get('XSRF-TOKEN');

        let name = document.getElementById(name);
        let dueDate = document.getElementById(dueDate);
        let courseId = document.getElementById(courseId);
        this.setState({assignment:{ name: name, dueDate: dueDate, courseId: courseId }});

        fetch(`${SERVER_URL}/course/${courseId}/assignment`, {
            method: 'POST', 
            headers: { 'Content-Type': 'application/json',
                       'X-XSRF-TOKEN': token }, 
            body: JSON.stringify({
                assignmentName: this.state.assignment.name,
                dueDate: this.state.assignment.dueDate,
                courseId: this.state.assignment.courseId })
        }).then(res => 
            {
                if (res.ok) {
                    toast.success("Assignment created.", {
                        position: toast.POSITION.BOTTOM_LEFT
                    });
                    // Go back to assignment.js?
                } else {
                    toast.error("Unable to add assignment", {
                        position: toast.POSITION.BOTTOM_LEFT
                    });
                }
            })
    };

    render() {
        const columns = [
            { field: 'name', headerName: 'Name', width: 250, editable:true},
            { field: 'dueDate', headerName: 'Due Date', width: 250, editable:true},
            { field: 'courseId', headerName: 'Course Id', width: 150 , editable:true}
            ];
          
        return(
            <div className="App">
                <div style={{width:'100%'}}>
                    For DEBUG:  display state.
                    {JSON.stringify(this.state.assignment)}
                </div>
                <form>
                <br></br>
                    Name:
                    <input type="text" id="name" name="name"></input>
                    <br></br>
                    Due Date:
                    <input type="text" id="dueDate" name="dueDate"></input>
                    <br></br>
                    Course Id:
                    <input type="text" id="courseId" name="courseId"></input>
                    <br></br>
                    <Button id="Save" variant="outlined" color="primary" style={{margin: 10}} onClick={this.handleSave} >
                        Save
                    </Button>
                </form>
                <ToastContainer autoClose={1500} />   
            </div>
        ); 
    };
}

export default newAssignment;

